// variables defined from settings
let secondsMode;
let secondsColoured;
let secondsWithColon;
let dateOnMain;
let dateOnSecs;
let weekDay;
let calWeek;
let upperCase;
let vectorFont;

// dynamic variables
let drawTimeout;
let queueMillis = 1000;
let secondsScreen = true;



//For development purposes
